mapper.delete:
DELETE FROM
	tb_blackboard
WHERE 
	enterprise_id = #{enterpriseId}
AND
	blackboard_id = #{blackboardId}

mapper.update:
		UPDATE 
		   	tb_blackboard 
		SET 	
			title = #{title},
			content = #{content},
			update_time = #{updateTime}

        WHERE
        	enterprise_id = #{enterpriseId}
        AND
        	blackboard_id = #{blackboardId}
