mapper.select:

SELECT
count(1)
FROM
tb_blackboard
WHERE
enterprise_id = #{enterpriseId}
AND
create_mobile = #{createMobile}
AND 
type = #{type}

mapper.delete:
DELETE FROM
tb_blackboard
WHERE
enterprise_id = #{enterpriseId}
AND
blackboard_id = #{blackboardId}

mapper.select:
SELECT t.blackboard_id FROM ( SELECT blackboard_id, update_time AS createTime FROM tb_blackboard tb WHERE enterprise_id = #{enterDeptId}
AND create_mobile = #{createMobile}
AND type = 0 AND NOT EXISTS ( SELECT 1 FROM tb_blcaklist tbl WHERE tbl.blacklter_id = tb.blackboard_id ) UNION ( SELECT blackboard_id, update_time AS createTime FROM tb_blackboard tb WHERE enterprise_id = #{enterDeptId}
AND create_mobile = #{createMobile}
AND type = 0 AND EXISTS ( SELECT 1 FROM tb_blcaklist tbl WHERE tbl.blacklter_id = tb.blackboard_id AND tbl.be_blacklter_id = #{mobile} ) ) ) t ORDER BY t.createTime DESC

SELECT type 
FROM tb_blackboard 
WHERE blackboard_id = #{blackboardId}

SELECT be_blacklter_id 
FROM tb_blcaklist 
WHERE blacklter_id = #{blackboardId}
